import React, { useState } from 'react';
import CardList from './components/CardList';
import SearchBox from './components/SearchBox';
import { fetchRobots } from './data/robots';

export default function App() {
  const [robots, setRobots] = useState([]);

  const handleSearch = (input) => {
    const searchingRobot = robots.filter((robot) => robot.name.includes(input));

    return searchingRobot;
  };

  const searchedRobot = handleSearch();

  // useEffect(() => {
  //   effect;
  // }, [input]);
  return (
    <>
      <h1>Robofriends</h1>
      <SearchBox handleSearch={handleSearch} />
      <CardList robots={searchedRobot.length ? searchedRobot : robots} />
    </>
  );
}
